<?php

namespace Odin\utils;

use Odin\utils\Logger;
use Odin\utils\Config;

class Errors
{

    private static $header;
    private static $body;
    private static $footer;

    const VIEWS_FOLDER = "data:image/png;base64,";

    const INIFILE = "data:image/png;base64,";

    const NOT_FOUND = "data:image/png;base64,";

    const ROUTE_NOT_FOUND = "data:image/png;base64,";

    const BUG = "data:image/png;base64,";

    const LOGO = " data:image/png;base64,";

    private static function selectErrorIcon($type)
    {
        switch ($type)
        {
            case 'notfound': return static::NOT_FOUND;
                break;
            case 'routenotfound': return static::ROUTE_NOT_FOUND;
                break;
            case 'folderundefined': return static::VIEWS_FOLDER;
                break;
            case 'bug': return static::BUG;
                break;
            case 'inifile': return static::INIFILE;
                break;
        }
    }

    private static function mount()
    {
        self::$header = "<!DOCTYPE html>
                        <html>
                            <head>
                                <meta charset=UTF-8>
                                <title>Erro</title>
                                <style>
                                    *{margin:0;padding:0}body{margin:0;font:12px/1.5 Helvetica,Arial,Verdana,sans-serif;background:#ebebeb}h1{margin:15px;font-size:48px;font-weight:400;line-height:48px}p{font-size:16px}strong{display:inline-block;width:65px}.content{padding:30px;text-align:center}.topbar{width:100%;background:#b30000;padding:30px}.topbar img{max-width:150px}.content .iconarea{width:100%;text-align:center}.content .iconarea img{min-width:120px;max-width:210px}
                                </style>
                            </head>
                            <div class=\"topbar\">
                                <img src=\"" . static::LOGO . "\">
                            </div>";
        self::$footer = "</body>
                      </html>";
        echo self::$header;
        echo self::$body;
        echo self::$footer;
    }

    public static function throwError($title, $errortext, $type)
    {
        self::writeLogs("EXCEPTIONS", $errortext);
        self::$body = "<div class=\"content\">
                      <div class=\"iconarea\">
                          <img src=\"" . self::selectErrorIcon($type) . "\">
                      </div>
                      <h1>{$title}</h1>
                      <p>{$errortext}</p>
                  </div>";

        self::mount();
    }

    public static function throwErrors(array $errors)
    {
        $type = "bug";
        $title = (count($errors) > 1) ? "Alguns erros foram encontrados!" : "Um erro foi encontrado!";
        self::$body = "<div class=\"content\">
                      <div class=\"iconarea\">
                          <img src=\"" . self::selectErrorIcon($type) . "\">
                      </div>
                      <h1>{$title}</h1>";
        foreach ($errors as $erro)
        {
            self::writeLogs("EXCEPTIONS", $erro);
            self::$body .= "<p>$erro</p>";
        }
        self::$body .= "</div>";

        self::mount();
    }

    private static function getPath()
    {
        $path = Config::get('remote_path');
        return $path;
    }

    private static function writeLogs(string $file, string $content)
    {
        if (Config::get('log_files')) {
            $logger = new Logger();
            $logger->open($file);
            $logger->write($content);
        }
    }

}
